Ext.define('ZJ02.view.InterviewNotice', {
  extend: 'Ext.Container',

  requires: [
    'Ext.TitleBar',
    'Ext.Button',
    'Ext.dataview.DataView',
    'Ext.XTemplate'
  ],

  config: {
    cls: 'zjs',
    layout: 'card',
    items: [
      {
        xtype: 'titlebar',
        cls: 'zjs-bg-green',
        docked: 'top',
        ui: 'zjs',
        title: '面试通知',
        items: [
          {
            xtype: 'button',
            height: 30,
            itemId: 'mybutton23',
            ui: 'plain',
            width: 30,
            icon: '',
            iconCls: 'zjs-icon-back',
            text: 'back'
          }
        ]
      },
      {
        xtype: 'dataview',
        cls: 'zjs-listview-interview-notice',
        id: 'list-msg-interview',
        itemId: 'mydataview3',
        minHeight: 180,
        scrollable: 'vertical',
        disableSelection: true,
        itemTpl: [
          '<div class="li">',
          '  <div class="c">',
          '    <h4>{companyName}</h4>',
          '    <p>  <span class="fl">应聘职位：<em>{positionName}</em></span> <span class="fr"><em>{date_fullyeardate}</em></span> </p>',
          '  </div>',
          '  <div class="i"> <span></span> </div>',
          '</div>',
          ''
        ]
      }
    ],
    listeners: [
      {
        fn: 'onMybutton23Tap',
        event: 'tap',
        delegate: '#mybutton23'
      },
      {
        fn: 'onMydataview2ItemTap1',
        event: 'itemtap',
        delegate: '#list-msg-interview'
      }
    ]
  },

  onMybutton23Tap: function(button, e, eOpts) {
    button.up('navigationview').pop();
  },

  onMydataview2ItemTap1: function(dataview, index, target, record, e, eOpts) {
    var item_data = record.data;
    change_page('InterviewNoticeDetail',dataview,item_data);
  },

  initialize: function() {
    this.callParent();
  },

  update: function(_p) {
    var _this = this;


    send_request( {
      api:'msg.interview.list',
      method:'POST',
      params:{
        pageSize:100
      },
      success:function(result, request){
        if( result.success ){
          switch( result.code ){
            case '0000':
              //成功
              console.log(result);
              _this.build_list(result.data);
              break;
            case '1001':
              //失败
              break;
            default:
              toast("errCode:"+result.code);
              break;
          }
        }else{

        }
      },
      fail:function(result){
        toast("您的网络似乎有点问题，请稍候再试！");
      },
      text:'加载中',
      silent:false,
      trigger:_this,
      check_login:true
    } );


  },

  build_list: function(data) {
    var x$list = x$('list-msg-interview');



    if(data && data.length){


      for( var i=0;i<data.length;i++ ){
        var date_ra = parse_date(data[i].auditionTime,'date_');
        jQuery.extend( data[i],date_ra );
      }


      x$list.removeCls('zj-list-no-data');
      x$list.setData(data);
    }else{
      x$list.addCls('zj-list-no-data');
      x$list.removeAll();
    }


    refresh_scroll(x$list);
  }

});